                               .font(.system(size: 16, weight: .medium))                                    .foregroundColor(.black)                            }                            .frame(maxWidth: .infinity)                            .frame(height: 200)                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                        }                    }                                        // Basic Info Section                    VStack(alignment: .leading, spacing: 16) {                        SectionHeader(title: "Event Details")                                                TextField("Event Title", text: $viewModel.title)                            .font(.system(size: 16))                            .padding()                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                                                TextEditor(text: $viewModel.description)                            .frame(height: 100)                            .padding(8)                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                            .overlay(                                Group {                                    if viewModel.description.isEmpty {                                        Text("Describe your event...")                                            .foregroundColor(.gray)                                            .padding(.leading, 12)                                            .padding(.top, 16)                                    }                                },                                alignment: .topLeading                            )                                                // Category Picker                        VStack(alignment: .leading, spacing: 8) {                            Text("Category")                                .font(.system(size: 14, weight: .semibold))                                .foregroundColor(.gray)                                                        Picker("Category", selection: $viewModel.category) {                                ForEach(EventCategory.allCases, id: \.self) { category in                                    Text(category.rawValue).tag(category)                                }                            }                            .pickerStyle(MenuPickerStyle())                            .padding()                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                        }                    }                                        // Date & Time Section                    VStack(alignment: .leading, spacing: 16) {                        SectionHeader(title: "When")                                                DatePicker("Start Date & Time", selection: $viewModel.eventDate, in: Date()...)                            .padding()                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                                                if viewModel.showEndDate {                            DatePicker("End Date & Time", selection: $viewModel.endDate, in: viewModel.eventDate...)                                .padding()                                .background(Color.gray.opacity(0.1))                                .cornerRadius(12)                        }                                                Button(action: {                            viewModel.showEndDate.toggle()                        }) {                            HStack {                                Image(systemName: viewModel.showEndDate ? "checkmark.circle.fill" : "circle")                                Text("Add End Time")                            }                            .font(.system(size: 14))                            .foregroundColor(.blue)                        }                    }                                        // Location Section                    VStack(alignment: .leading, spacing: 16) {                        SectionHeader(title: "Where")                                                Button(action: {                            viewModel.showLocationSearch = true                        }) {                            HStack {                                Image(systemName: "mappin.circle.fill")                                    .foregroundColor(.red)                                    .font(.system(size: 20))                                                                if let location = viewModel.selectedLocation {                                    VStack(alignment: .leading, spacing: 4) {                                        Text(location.name ?? "Location")                                            .foregroundColor(.black)                                            .font(.system(size: 16, weight: .medium))                                        if let address = location.placemark.title {                                            Text(address)                                                .font(.system(size: 13))                                                .foregroundColor(.gray)                                        }                                    }                                } else {                                    Text("Add Event Location")                                        .foregroundColor(.gray)                                }                                                                Spacer()                                                                Image(systemName: "chevron.right")                                    .foregroundColor(.gray)                                    .font(.system(size: 14))                            }                            .padding()                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                        }                    }                                        // Additional Settings                    VStack(alignment: .leading, spacing: 16) {                        SectionHeader(title: "Settings")                                                Toggle(isOn: $viewModel.isPublic) {                            VStack(alignment: .leading, spacing: 4) {                                Text("Public Event")                                    .font(.system(size: 16, weight: .medium))                                Text("Anyone can see and join")                                    .font(.system(size: 13))                                    .foregroundColor(.gray)                            }                        }                        .padding()                        .background(Color.gray.opacity(0.1))                        .cornerRadius(12)                                                Toggle(isOn: $viewModel.requiresApproval) {                            VStack(alignment: .leading, spacing: 4) {                                Text("Approval Required")                                    .font(.system(size: 16, weight: .medium))                                Text("You approve who can attend")                                    .font(.system(size: 13))                                    .foregroundColor(.gray)                            }                        }                        .padding()                        .background(Color.gray.opacity(0.1))                        .cornerRadius(12)                        .disabled(!viewModel.isPublic)                                                Toggle(isOn: $viewModel.hasCapacity) {                            Text("Set Attendee Limit")                                .font(.system(size: 16, weight: .medium))                        }                        .padding()                        .background(Color.gray.opacity(0.1))                        .cornerRadius(12)                                                if viewModel.hasCapacity {                            HStack {                                Text("Max Attendees:")                                    .font(.system(size: 16))                                Spacer()                                TextField("100", value: $viewModel.maxAttendees, format: .number)                                    .keyboardType(.numberPad)                                    .multilineTextAlignment(.trailing)                                    .frame(width: 80)                            }                            .padding()                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                        }                    }                                        // Tags Section                    VStack(alignment: .leading, spacing: 16) {                        SectionHeader(title: "Tags (Optional)")                                                TextField("Add tags separated by commas", text: $viewModel.tagsInput)                            .font(.system(size: 16))                            .padding()                            .background(Color.gray.opacity(0.1))                            .cornerRadius(12)                                                if !viewModel.parsedTags.isEmpty {                            ScrollView(.horizontal, showsIndicators: false) {                                HStack(spacing: 8) {                                    ForEach(viewModel.parsedTags, id: \.self) { tag in                                        Text("#\(tag)")                                            .font(.system(size: 13))                                            .padding(.horizontal, 12)                                            .padding(.vertical, 6)                                            .background(Color.black)                                            .foregroundColor(.white)                                            .cornerRadius(16)                                    }                                }                            }                        }                    }                                        // Error Message                    if !viewModel.errorMessage.isEmpty {                        Text(viewModel.errorMessage)                            .font(.system(size: 14))                            .foregroundColor(.red)                            .padding()                    }                }                .padding()            }            .navigationTitle("Create Event")            .navigationBarTitleDisplayMode(.inline)            .toolbar {                ToolbarItem(placement: .navigationBarLeading) {                    Button("Cancel") {                        dismiss()                    }                }                                ToolbarItem(placement: .navigationBarTrailing) {                    Button("Create") {                        viewModel.createEvent {                            dismiss()                        }                    }                    .disabled(viewModel.isCreating)                    .bold()                }            }            .sheet(isPresented: $viewModel.showImagePicker) {                ImagePicker(image: $viewModel.selectedImage)            }            .sheet(isPresented: $viewModel.showLocationSearch) {                LocationSearchView(selectedLocation: $viewModel.selectedLocation)            }        }    }}struct SectionHeader: View {    let title: String        var body: some View {        Text(title)            .font(.system(size: 18, weight: .bold))            .foregroundColor(.black)    }}class CreateEventViewModel: ObservableObject {    @Published var title = ""    @Published var description = ""    @Published var category: EventCategory = .social    @Published var eventDate = Date().addingTimeInterval(3600) // 1 hour from now    @Published var endDate = Date().addingTimeInterval(7200)   // 2 hours from now    @Published var showEndDate = false    @Published var selectedLocation: MKMapItem?    @Published var selectedImage: UIImage?    @Published var isPublic = true    @Published var requiresApproval = false    @Published var hasCapacity = false    @Published var maxAttendees: Int = 100    @Published var tagsInput = ""    @Published var clubId: String?        @Published var showImagePicker = false    @Published var showLocationSearch = false    @Published var isCreating = false    @Published var errorMessage = ""        var parsedTags: [String] {        tagsInput            .split(separator: ",")            .map { $0.trimmingCharacters(in: .whitespacesAndNewlines) }            .filter { !$0.isEmpty }    }        func createEvent(completion: @escaping () -> Void) {        // Validation        guard !title.isEmpty else {            errorMessage = "Please enter an event title"            return        }                guard let location = selectedLocation else {            errorMessage = "Please select a location"            return        }                errorMessage = ""        isCreating = true                Task {            do {                let coordinate = location.placemark.coordinate                let address = [                    location.placemark.name,                    location.placemark.locality,                    location.placemark.administrativeArea,                    location.placemark.postalCode                ].compactMap { $0 }.joined(separator: ", ")                                _ = try await EventService.shared.createEvent(                    title: title,                    description: description,                    eventDate: eventDate,                    endDate: showEndDate ? endDate : nil,                    location: coordinate,                    address: address,                    category: category,                    image: selectedImage,                    clubId: clubId,                    isPublic: isPublic,                    requiresApproval: requiresApproval,                    maxAttendees: hasCapacity ? maxAttendees : nil,                    tags: parsedTags                )                                await MainActor.run {                    isCreating = false                    completion()                }            } catch {                await MainActor.run {                    isCreating = false                    errorMessage = "Error: \(error.localizedDescription)"                    print("Error creating event: \(error)")                }            }        }    }}#Preview {    CreateEventView()}