////  Event.swift//  FinalProject////  Created by Enhanced Features//import Foundationimport ParseSwiftimport CoreLocationstruct Event: ParseObject {    var objectId: String?    var createdAt: Date?    var updatedAt: Date?    var ACL: ParseACL?    var originalData: Data?        // Basic Event Info    var title: String?    var description: String?    var hostId: String?           // User who created the event    var hostUsername: String?     // Cached for display    var clubId: String?           // Optional: associated club    var clubName: String?         // Cached club name        // Event Details    var eventDate: Date?          // When the event happens    var endDate: Date?            // Event end time    var location: ParseGeoPoint?  // Where the event is    var address: String?          // Human-readable address    var category: String?         // Sport, Music, Tech, etc.        // Media    var imageURL: String?    var imageFile: ParseFile?        // Engagement    var attendeesCount: Int?      // Number of people going    var interestedCount: Int?     // Number of people interested    var maxAttendees: Int?        // Optional capacity limit        // Settings    var isPublic: Bool?           // Public or private event    var requiresApproval: Bool?   // Does host need to approve attendees    var tags: [String]?           // Event tags for discovery        var coordinate: CLLocationCoordinate2D {        guard let location = location else {            return CLLocationCoordinate2D(latitude: 0, longitude: 0)        }        return CLLocationCoordinate2D(latitude: location.latitude, longitude: location.longitude)    }}// Event attendance statusstruct EventAttendee: ParseObject {    var objectId: String?    var createdAt: Date?    var updatedAt: Date?    var ACL: ParseACL?    var originalData: Data?        var eventId: String?    var userId: String?    var username: String?         // Cached    var status: String?           // "going", "interested", "maybe"    var checkedIn: Bool?          // Did they show up?}enum EventCategory: String, CaseIterable {    case sport = "Sport"    case music = "Music"    case tech = "Tech"    case arts = "Arts"    case food = "Food & Drink"    case gaming = "Gaming"    case business = "Business"    case education = "Education"    case social = "Social"    case outdoor = "Outdoor"    case wellness = "Wellness"    case other = "Other"}enum AttendeeStatus: String {    case going = "going"    case interested = "interested"    case maybe = "maybe"}