import SwiftUIimport PhotosUIstruct ProfileView: View {    @Binding var isLoggedIn: Bool    @StateObject private var viewModel = ProfileViewModel()    @State private var showEditProfile = false    @State private var showSettings = false        var body: some View {        NavigationView {            ScrollView {                VStack(spacing: 24) {                    // Profile Header                    VStack(spacing: 16) {                        // Profile Picture - FIXED                        ZStack(alignment: .bottomTrailing) {                            if let imageURL = viewModel.currentUser?.profileImageURL,                               !imageURL.isEmpty,                               let url = URL(string: imageURL) {                                AsyncImage(url: url) { phase in                                    switch phase {                                    case .empty:                                        Circle()                                            .fill(Color.gray.opacity(0.2))                                            .frame(width: 100, height: 100)                                            .overlay(ProgressView())                                    case .success(let image):                                        image                                            .resizable()                                            .scaledToFill()                                            .frame(width: 100, height: 100)                                            .clipShape(Circle())                                    case .failure:                                        Circle()                                            .fill(Color.black)                                            .frame(width: 100, height: 100)                                            .overlay(                                                Text(viewModel.currentUser?.username?.prefix(1).uppercased() ?? "?")                                                    .font(.system(size: 40, weight: .bold))                                                    .foregroundColor(.white)                                            )                                    @unknown default:                                        Circle()                                            .fill(Color.black)                                            .frame(width: 100, height: 100)                                            .overlay(                                                Text(viewModel.currentUser?.username?.prefix(1).uppercased() ?? "?")                                                    .font(.system(size: 40, weight: .bold))                                                    .foregroundColor(.white)                                            )                                    }                                }                                .id(imageURL)                            } else {                                Circle()                                    .fill(Color.black)                                    .frame(width: 100, height: 100)                                    .overlay(                                        Text(viewModel.currentUser?.username?.prefix(1).uppercased() ?? "?")                                            .font(.system(size: 40, weight: .bold))                                            .foregroundColor(.white)                                    )                            }                                                        // Edit button overlay                            Button(action: {                                showEditProfile = true                            }) {                                Image(systemName: "pencil.circle.fill")                                    .font(.system(size: 28))                                    .foregroundColor(.black)                                    .background(Circle().fill(Color.white))                            }                            .offset(x: 5, y: 5)                        }                                                VStack(spacing: 8) {                            Text(viewModel.currentUser?.username ?? "User")                                .font(.system(size: 24, weight: .bold))                                                        if let bio = viewModel.currentUser?.bio, !bio.isEmpty {                                Text(bio)                                    .font(.system(size: 14))                                    .foregroundColor(.gray)                                    .multilineTextAlignment(.center)                                    .padding(.horizontal, 40)                            }                        }                    }                    .padding(.top, 20)                                        // Stats                    HStack(spacing: 0) {                        StatColumn(                            value: "\(viewModel.currentUser?.postsCount ?? 0)",                            label: "Posts"                        )                                                Divider()                            .frame(height: 40)                                                StatColumn(                            value: "\(viewModel.currentUser?.followersCount ?? 0)",                            label: "Followers"                        )                                                Divider()                            .frame(height: 40)                                                StatColumn(                            value: "\(viewModel.currentUser?.followingCount ?? 0)",                            label: "Following"                        )                    }                    .padding()                    .background(Color.gray.opacity(0.1))                    .cornerRadius(12)                    .padding(.horizontal)                                        // Edit Profile Button                    Button(action: {                        showEditProfile = true                    }) {                        HStack {                            Image(systemName: "person.crop.circle")                            Text("Edit Profile")                                .font(.system(size: 16, weight: .semibold))                        }                        .foregroundColor(.black)                    }                    .frame(maxWidth: .infinity)                    .padding(.vertical, 14)                    .background(Color.gray.opacity(0.2))                    .cornerRadius(12)                    .padding(.horizontal)                                        Divider()                                        // User's Posts Grid                    VStack(alignment: .leading, spacing: 16) {                        Text("My Posts")                            .font(.system(size: 20, weight: .bold))                            .padding(.horizontal)                                                if viewModel.isLoading {                            ProgressView()                                .padding(.top, 40)                        } else if viewModel.userPosts.isEmpty {                            VStack(spacing: 16) {                                Image(systemName: "photo.on.rectangle")                                    .font(.system(size: 50))                                    .foregroundColor(.gray)                                Text("No posts yet")                                    .font(.system(size: 16))                                    .foregroundColor(.gray)                            }                            .frame(maxWidth: .infinity)                            .padding(.top, 40)                        } else {                            LazyVGrid(columns: [                                GridItem(.flexible()),                                GridItem(.flexible()),                                GridItem(.flexible())                            ], spacing: 2) {                                ForEach(viewModel.userPosts) { post in                                    if let imageURL = post.imageURL, let url = URL(string: imageURL) {                                        AsyncImage(url: url) { phase in                                            switch phase {                                            case .empty:                                                Rectangle()                                                    .fill(Color.gray.opacity(0.2))                                                    .aspectRatio(1, contentMode: .fit)                                                    .overlay(ProgressView())                                            case .success(let image):                                                image                                                    .resizable()                                                    .scaledToFill()                                                    .aspectRatio(1, contentMode: .fill)                                                    .clipped()                                            case .failure:                                                Rectangle()                                                    .fill(Color.gray.opacity(0.2))                                                    .aspectRatio(1, contentMode: .fit)                                            @unknown default:                                                EmptyView()                                            }                                        }                                    }                                }                            }                        }                    }                }            }            .navigationTitle("Profile")            .toolbar {                ToolbarItem(placement: .navigationBarTrailing) {                    Button(action: {                        showSettings = true                    }) {                        Image(systemName: "gearshape.fill")                            .foregroundColor(.black)                    }                }            }            .sheet(isPresented: $showEditProfile) {                EditProfileView()            }            .sheet(isPresented: $showSettings) {                SettingsView(isLoggedIn: $isLoggedIn)            }        }        .onAppear {            viewModel.loadUserData()        }        .onReceive(NotificationCenter.default.publisher(for: NSNotification.Name("ProfileUpdated"))) { _ in            viewModel.loadUserData()        }    }}struct StatColumn: View {    let value: String    let label: String        var body: some View {        VStack(spacing: 4) {            Text(value)                .font(.system(size: 20, weight: .bold))            Text(label)                .font(.system(size: 12))                .foregroundColor(.gray)        }        .frame(maxWidth: .infinity)    }}class ProfileViewModel: ObservableObject {    @Published var currentUser: User?    @Published var userPosts: [Post] = []    @Published var isLoading = false        init() {        currentUser = User.current    }        func loadUserData() {        Task {            do {                if let userId = User.current?.objectId {                    let allUsers = try await User.query().find()                    if let updatedUser = allUsers.first(where: { $0.objectId == userId }) {                        await MainActor.run {                            self.currentUser = updatedUser                        }                    }                }            } catch {                print("Error refreshing user: \(error)")            }        }                guard let userId = currentUser?.objectId else { return }                isLoading = true                Task {            do {                let posts = try await SocialService.shared.getUserPosts(userId: userId)                await MainActor.run {                    self.userPosts = posts                    self.isLoading = false                }            } catch {                await MainActor.run {                    self.isLoading = false                    print("Error loading user posts: \(error)")                }            }        }    }}#Preview {    ProfileView(isLoggedIn: .constant(true))}