import SwiftUIstruct SettingsView: View {    @Binding var isLoggedIn: Bool    @AppStorage("isDarkMode") private var isDarkMode = false    @State private var showEditProfile = false    @State private var showSignOutAlert = false    @Environment(\.dismiss) var dismiss        var body: some View {        NavigationView {            List {                // Profile Section                Section {                    Button(action: {                        showEditProfile = true                    }) {                        HStack {                            Image(systemName: "person.circle.fill")                                .foregroundColor(.black)                                .font(.system(size: 20))                            Text("Edit Profile")                                .foregroundColor(.primary)                            Spacer()                            Image(systemName: "chevron.right")                                .foregroundColor(.gray)                                .font(.system(size: 14))                        }                    }                } header: {                    Text("Profile")                }                                // Appearance Section                Section {                    Toggle(isOn: $isDarkMode) {                        HStack {                            Image(systemName: "moon.fill")                                .foregroundColor(.black)                                .font(.system(size: 20))                            Text("Dark Mode")                        }                    }                } header: {                    Text("Appearance")                }                                // Account Section                Section {                    Button(action: {                        showSignOutAlert = true                    }) {                        HStack {                            Image(systemName: "rectangle.portrait.and.arrow.right")                                .foregroundColor(.red)                                .font(.system(size: 20))                            Text("Sign Out")                                .foregroundColor(.red)                        }                    }                } header: {                    Text("Account")                }                                // App Info Section                Section {                    HStack {                        Text("Version")                        Spacer()                        Text("1.0.0")                            .foregroundColor(.gray)                    }                } header: {                    Text("About")                }            }            .navigationTitle("Settings")            .navigationBarTitleDisplayMode(.inline)            .toolbar {                ToolbarItem(placement: .navigationBarTrailing) {                    Button("Done") {                        dismiss()                    }                }            }            .sheet(isPresented: $showEditProfile) {                EditProfileView()            }            .alert("Sign Out", isPresented: $showSignOutAlert) {                Button("Cancel", role: .cancel) { }                Button("Sign Out", role: .destructive) {                    handleSignOut()                }            } message: {                Text("Are you sure you want to sign out?")            }        }    }        func handleSignOut() {        Task {            do {                try await ParseService.shared.signOut()                await MainActor.run {                    isLoggedIn = false                    dismiss()                }            } catch {                print("Sign out error: \(error)")            }        }    }}#Preview {    SettingsView(isLoggedIn: .constant(true))}